mapper.update:
UPDATE files
set file_name = #{fileName}
WHERE file_id = #{fileId}

UPDATE folders
set folder_name = #{folderName}
WHERE folder_id = #{folderId}

UPDATE files
    SET parent_id = #{parentId}
    WHERE file_id = #{id}

UPDATE folders
    SET parent_id = #{parentId}
    WHERE folder_id = #{id}

update files
set is_deleted = #{status}
where file_id = #{fileId}

update folders
set is_deleted = 0
where folder_id = #{folderId}

mapper.delete:
delete from collections
where folder_id=#{folderId}
and user_id=#{userId}

delete from collections
where file_id=#{fileId}
  and user_id=#{userId}

mapper.select:
SELECT
    (SELECT COUNT(*) FROM files WHERE parent_id = #{parentId}) +
    (SELECT COUNT(*) FROM folders WHERE parent_id = #{parentId}) AS total_count;

SELECT *
FROM files
WHERE parent_id = #{parentId}
  AND file_type LIKE 'image/%' AND is_deleted = 0;

SELECT tag FROM folders WHERE tag IS NOT NULL AND tag != '' AND is_deleted = 0
UNION
SELECT tag FROM files WHERE tag IS NOT NULL AND tag != '' AND is_deleted = 0;

SELECT SUM(file_size) AS total_file_size_bytes
FROM files
WHERE is_deleted = 0;

SELECT SUM(file_size) AS total_file_size_bytes
FROM files
WHERE is_deleted = 1;

SELECT f.*
FROM files f
         JOIN collections c ON f.file_id = c.file_id
WHERE f.is_deleted = 0
  AND c.user_id = #{userId}
  AND f.file_type LIKE 'image/%';
